    align-items: flex-start;
    gap: 1rem;
    transition: all 0.2s;
}

.metric-card:hover {
    box-shadow: var(--shadow-md);
    border-color: var(--border-hover);
}

.metric-icon {
    width: 48px;
    height: 48px;
    border-radius: 12px;
