    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.metric-content {
    flex: 1;
}

.metric-label {
    font-size: 0.875rem;
    color: var(--text-medium);
    margin-bottom: 0.25rem;
}

.metric-value {
    font-size: 2rem;
    font-weight: 700;
    color: var(--text-dark);
}

.recent-quotes-section {
    background: var(--bg-white);
    border: 1px solid var(--border);
    border-radius: var(--border-radius);
    overflow: hidden;
}

.section-header {
    padding: 1.5rem;
    border-bottom: 1px solid var(--border);
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.section-header h3 {
    font-size: 1.125rem;
    font-weight: 600;
    color: var(--text-dark);
}

.quotes-table {
    min-height: 200px;
    border-radius: var(--border-radius);
    overflow: hidden;
}

.quotes-table table {
    width: 100%;
